import React, { useState } from "react";
import Search from "./Search";
import SideBar from "./SideBar";
import ResultSection from "./ResultSection";

function SearchSection() {

    const [queryState, setQueryState] = useState([]);
    const [resultQueryState, setResultQueryState] = useState([]);
    
    var resultQuery = [];
    resultQuery = resultQueryState.map((item, i) => {
      return (<ResultSection key={i} data={item} />);
    });
    var sideBarQuery = [];
    sideBarQuery = queryState.map((item, i) => {
      return (<SideBar key={i} data={item} setResultQueryState={setResultQueryState} />);
    });

    return (
        <div className="searchWrapper">
            <div className="sideBarWrapper">
            {sideBarQuery}
            </div>
            <Search 
            queryState={queryState} 
            setQueryState={setQueryState} 
            setResultQueryState={setResultQueryState} />
            <div className="resultWrapper">
            {resultQuery}
            </div>
        </div>
    );
}








export default SearchSection;
