import React, { useState } from "react";
import { FormControl } from "react-bootstrap";

function InnerSearch(props) {

    const [totalSearchResult] = useState(0);
    console.log(props);
    function handleChange(e) {
        var value = e.target.value;
        var innerSearchResult = [];
        var resultArray = props.resultQueryState.slice();
        var result = [];
        innerSearchResult = props.resultQueryState.map((item, i) => {
            result = flatMap(item.split(value), function (part) {
                console.log(part);
              });
            resultArray[i].title = item.title.replace(e.target.value, <div className="innerSearchResult">{e.target.value}</div>);
        });
        //console.log(resultArray);
    }

    function flatMap(array, fn) {
        var result = [];
        for (var i = 0; i < array.length; i++) {
          var mapping = fn(array[i]);
          result = result.concat(mapping);
        }
        return result;
      }

      
    return (
        <div className="innerSearchBox">
            <FormControl
                placeholder="Search in results" onChange={(e) => handleChange(e)}
            />
    <div className="totalSearchResult">{totalSearchResult}</div>
        </div>
    );
}

export default InnerSearch;
