import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

function Search(props) {
    const [searchState, setSearchState] = useState("");
    return (
        <div className="searchWrapper">
            <Form>
                <Form.Group controlId="Search">
                    <Form.Control type="search" name="search" onChange={(e) => handleChange(e)} placeholder="" />
                </Form.Group>
                <Button variant="primary" onClick={(e) => searchSubmit(e)} type="submit">
                    Search
                </Button>
            </Form>
        </div>
    );

    function searchSubmit(e){
        e.preventDefault();
        fetch("http://localhost/ManageRequest/getApiData", {
            method: "POST",
            withCredentials: true,
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            credentials: "include",
            body: JSON.stringify({
                search: searchState
            }),
        }).then((response) => response.json())
            .then((result) => {
                if(searchState.length > 0){
                updateQueryState();
                }
                props.setResultQueryState(result);
            });
    }

    function updateQueryState(){
        var queryArray = props.queryState.slice();
        var queryArrayCheck = checkDoubleInArray(queryArray);
        //console.log(queryArrayCheck);
        if(queryArrayCheck){
            queryArray.push(searchState); 
        }
        props.setQueryState(queryArray);
    }

    function checkDoubleInArray(queryArray){
        var status = 1;

        //console.log(searchState);
        //console.log(queryArray);
        queryArray.map((item, i) => {
            console.log(searchState == queryArray);
            console.log(typeof(searchState));
            console.log(typeof(item.type));
            if(searchState === item && i === queryArray.length){
                status = -1;
            }
        });
        return status;
    }

    function handleChange(e) {
        setSearchState(e.target.value);
    }

}

export default Search;
