import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

function Search(props) {


    console.log(props);
    const [searchState, setSearchState] = useState("");
    return (
        <div className="searchWrapper">
            <Form>
                <Form.Group controlId="Search">
                    <Form.Control type="search" name="search" onChange={(e) => handleChange(e)} placeholder="" />
                </Form.Group>
                <Button variant="primary" onClick={(e) => searchSubmit(e)} type="submit">
                    Search
                </Button>
            </Form>
        </div>
    );

    function searchSubmit(e){
        e.preventDefault();
        fetch("http://localhost/ManageRequest/getApiData", {
            method: "POST",
            withCredentials: true,
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            credentials: "include",
            body: JSON.stringify({
                search: searchState
            }),
        }).then((response) => response.json())
            .then((result) => {
                props.setQueryState(searchState);
                props.setResultQueryState(result);
            });
    }

    function handleChange(e) {
        setSearchState(e.target.value);
    }

}

export default Search;
