import React, { useState } from "react";
import { FormControl } from "react-bootstrap";

function InnerSearch(props) {

    const [totalSearchResult, setTotalSearchResult] = useState(0);
    const [innerSearchResultState, setInnerSearchResultState] = useState(props.resultQueryState);


    function handleChange(e) {
        var value = e.target.value;
        var innerSearchResult = [];
        var resultArray = props.resultQueryState.slice(); 
        innerSearchResult = props.resultQueryState.map((item, i) => {
            changeResultText(item.title, value);
        });

        console.log(innerSearchResult);
        //setInnerSearchResultState(innerSearchResult);
        //console.log(resultArray);
    }
      
    //console.log(innerSearchResult);
    function changeResultText(title, value){
        const reactStringReplace = require('react-string-replace');
        var result = [];
        result = reactStringReplace(title.toLowerCase(), value.toLowerCase(), (match, i) => (
            <div key={i} className="innerSearch">{match}</div>
          ));
          return result;
    }

    return (
        <div className="innerSearchBox">
            <FormControl
                placeholder="Search in results" onChange={(e) => handleChange(e)}
            />
            <div className="test">{innerSearchResultState}</div>
    <div className="totalSearchResult">{totalSearchResult}</div>
        </div>
    );
}

export default InnerSearch;
